// 変数宣言
let mutable = "可変";
const immutable = "不変参照";

// テンプレート文字列
const name = "World";
const greeting = `Hello, ${name}!`;

// 配列
const arr = [1, 2, 3];
arr.map(x => x * 2);
arr.filter(x => x > 1);
arr.reduce((acc, x) => acc + x, 0);

// オブジェクト
const obj = { name, age: 30 };
const { name: userName } = obj;  // 分割代入

// Promise
fetch('/api/data')
    .then(res => res.json())
    .then(data => console.log(data))
    .catch(err => console.error(err));

// async/await
async function fetchData() {
    try {
        const res = await fetch('/api/data');
        const data = await res.json();
        return data;
    } catch (error) {
        console.error(error);
    }
}

// 並列実行
const [r1, r2] = await Promise.all([fetch1(), fetch2()]);

class Person {
    #secret = "hidden";  // プライベートフィールド
    static count = 0;    // 静的プロパティ

    constructor(name, age) {
        this.name = name;
        this.age = age;
    }

    greet() {
        return `Hello, I'm ${this.name}`;
    }

    get info() {
        return `${this.name}, ${this.age}`;
    }
}

class Employee extends Person {
    constructor(name, age, dept) {
        super(name, age);
        this.dept = dept;
    }
}
