/* 基础过渡 */
.button {
  background: #3b82f6;
  transform: scale(1);
  transition: all 0.3s ease;
}

.button:hover {
  background: #2563eb;
  transform: scale(1.05);
}

/* 多属性过渡 */
.card {
  opacity: 1;
  transform: translateY(0);
  transition: 
    opacity 0.3s ease,
    transform 0.3s ease;
}

.card:hover {
  opacity: 0.9;
  transform: translateY(-4px);
}

/* 定义关键帧 */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
}

/* 应用动画 */
.fade-in {
  animation: fadeIn 0.5s ease-out forwards;
}

.spinner {
  animation: spin 1s linear infinite;
}

.pulse {
  animation: pulse 2s ease-in-out infinite;
}
